aNbN:
// accepts strings of the form a^nb^n for some n >= 0
// e.g. blank, ab, aabb, aaabbb, etc.
alphabet = {a, b}
module aNbN {
    // empty string => base case, accept
    if blank {
        accept
    }
    // cannot start with b
    if b {
        reject
    }
    // starts with a => remove the last b
    if a {
        changeto blank
        move right
        // move to the end
        while a, b {
            move right
        } if blank {
            move left
            // the last letter must be a b
            if a, blank {
                reject
            } if b {
                changeto blank
                move left
                // move back and restart
                while a, b {
                    move left
                } if blank {
                    move right
                    goto aNbN
                }
            }
        }
    }
}

isDiv2:
// checks whether a binary number is divisible by 2
alphabet = {0, 1}
module isDiv2 {
    // move to the end
    while 0, 1 {
        move right
    } if blank {
        move left
        // check last letter is 0
        if 0 {
            accept
        } if 1, blank {
            reject
        }
    }
}

isDiv2Rec:
// checks whether a binary number is divisible by 2 recursively
alphabet = {0, 1}
module isDiv2Rec {
    // recursive case: not at the end => move closer to the end
    if 0, 1 {
        move right
        goto isDiv2Rec
    } 
    // base case: at the end => check final letter 0
    if blank {
        move left
        if 0 {
            accept
        } if 1, blank {
            reject
        }
    }
}

