isDiv2:
// checks whether a binary number is divisible by 2
alphabet = {0, 1}
module isDiv2 {
    while 0, 1 {
        move right
    } if blank {
        move left
        if 0 {
            accept
        } if 1, blank {
            reject
        }
    }
}

isDiv4:
// checks whether a binary number is divisible by 4
alphabet = {0, 1}
module isDiv4 {
    while 0, 1 {
        move right
    } if blank {
        move left
        if 0 {
            move left
            if 0, blank {
                accept
            } if 1 {
                reject
            }
        } if 1, blank {
            reject
        }
    }
}

aNbN:
// accepts strings of the form a^nb^n for some n >= 0
alphabet = {a, b}
module aNbN {
    // empty string => base case, accept
    if blank {
        accept
    }
    // cannot start with b
    if b {
        reject
    }
    // starts with a => remove the last b
    if a {
        changeto blank
        move right
        // move to the end
        while a, b {
            move right
        } if blank {
            move left
            // the last letter must be a b
            if a, blank {
                reject
            } if b {
                changeto blank
                move left
                // move back and restart
                while a, b {
                    move left
                } if blank {
                    move right
                    goto aNbN
                }
            }
        }
    }
}
